,Psubscriber_id IN NUMBER
,Pupdate_count  OUT NUMBER)
as
             Begin
    UPDATE dms_topic_entry te 
    SET te.read_state = Pread_lock 
    WHERE ROWNUM < Pbatch_size
      AND te.delivery_date < Pdelivery_date 
